SELECT id_factura as "ID",referencia,IF(C.razon_social IS NOT NULL,CONCAT("Cli [",C.id_cliente,"]: ",C.razon_social),CONCAT("Prov [",P.id_proveedor,"]: ",P.razon_social)) as "RazonSocial",F.fecha,F.base_imponible as "BI",F.monto_total as "Tot",FP.nombre as "FPago",IF(F.factura_abono,"Si","No") as "Abono" FROM Facturas F LEFT JOIN biomundo_contabilidad.Asiento A ON (F.id_asiento=A.id_asiento) LEFT JOIN Clientes C ON (F.id_cliente=C.id_cliente) LEFT JOIN Proveedores P ON (P.id_proveedor=F.id_proveedor) JOIN Forma_pago FP ON (F.id_forma_pago=FP.id_forma_pago) WHERE F.estado=1 AND A.id_asiento IS NULL AND F.id_serie NOT IN (7,8,12) ORDER BY  fecha desc 
